package Loops;

public class doWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 1;

		do {
			System.out.println("Count is " + count);
			count++;
		} while (count <= 10);

		int c1 = 10;

		while (c1 >= 1) {
			System.out.println("c1 is  : " + c1);
			c1--;
		}
	}

}
